import { validateDatasetAliasName } from '../../../actions/dataset/alias/validateDatasetAliasName';
export const ALIAS_PREFIX = '~';
export function listAliases(client) {
  return client.request({
    uri: '/aliases'
  });
}
export function createAlias(client, aliasName, datasetName) {
  return modify(client, 'PUT', aliasName, datasetName ? {
    datasetName
  } : undefined);
}
export function updateAlias(client, aliasName, datasetName) {
  return modify(client, 'PATCH', aliasName, datasetName ? {
    datasetName
  } : undefined);
}
export function unlinkAlias(client, aliasName) {
  validateDatasetAliasName(aliasName);
  return modify(client, 'PATCH', "".concat(aliasName, "/unlink"), {});
}
export function removeAlias(client, aliasName) {
  return modify(client, 'DELETE', aliasName);
}

function modify(client, method, aliasName, body) {
  return client.request({
    method,
    uri: "/aliases/".concat(aliasName),
    body
  });
}