export const getProviderTitle = provider => {
  if (provider === 'google') {
    return 'Google';
  }

  if (provider === 'github') {
    return 'GitHub';
  }

  if (provider === 'sanity') {
    return 'Sanity';
  }

  if (provider !== null && provider !== void 0 && provider.startsWith('saml-')) {
    return 'SAML/SSO';
  }

  return undefined;
};