import { from } from 'rxjs';
import { useSource } from '../../studio';
import { useUserStore } from '../datastores';
import { createHookFromObservableFactory } from '../../util';
const useUserViaUserStore = createHookFromObservableFactory((userStore, userId) => from(userStore.getUser(userId)));
export function useUser(userId) {
  const userStore = useUserStore();
  return useUserViaUserStore(userStore, userId);
}
export function useCurrentUser() {
  const {
    currentUser
  } = useSource();
  return currentUser;
}