import { addDays, eachWeekOfInterval, getWeek, lastDayOfMonth, startOfMonth } from 'date-fns';
import { TAIL_WEEKDAYS } from './constants';
export const getWeekStartsOfMonth = date => {
  const firstDay = startOfMonth(date);
  return eachWeekOfInterval({
    start: firstDay,
    end: lastDayOfMonth(firstDay)
  });
};
export const getWeekDaysFromWeekStarts = weekStarts => {
  return weekStarts.map(weekStart => [weekStart, ...TAIL_WEEKDAYS.map(d => addDays(weekStart, d))]);
};
export const getWeeksOfMonth = date => getWeekDaysFromWeekStarts(getWeekStartsOfMonth(date)).map(days => ({
  number: getWeek(days[0]),
  days
}));
export const formatTime = (hours, minutes) => "".concat("".concat(hours).padStart(2, '0'), ":").concat("".concat(minutes).padStart(2, '0'));