import { map, scan } from 'rxjs/operators';
import { fromMutationPatches, toMutationPatches } from '../utils/mutationPatch';

function prepareMutationEvent(event) {
  const patches = event.mutations.map(mut => mut.patch).filter(Boolean);
  return {
    type: 'mutation',
    document: event.document,
    mutations: event.mutations,
    patches: fromMutationPatches(event.origin, patches),
    origin: event.origin
  };
}

function prepareRebaseEvent(event) {
  const patches = [{
    id: event.document._id,
    set: event.document
  }];
  return {
    type: 'mutation',
    document: event.document,
    mutations: patches.map(patch => ({
      patch
    })),
    patches: fromMutationPatches('internal', patches),
    origin: event.origin // @todo

  };
}

function wrap(document) {
  const events$ = document.events.pipe(map(event => {
    if (event.type === 'mutation') {
      return prepareMutationEvent(event);
    } else if (event.type === 'rebase') {
      return prepareRebaseEvent(event);
    }

    return event;
  }), scan((prevEvent, currentEvent) => {
    const deletedSnapshot = prevEvent && prevEvent.type === 'mutation' && prevEvent.document !== null && currentEvent.type === 'mutation' && currentEvent.document === null ? prevEvent.document : null;
    return { ...currentEvent,
      deletedSnapshot
    };
  }, null));
  return { ...document,
    events: events$,

    patch(patches) {
      document.patch(toMutationPatches(patches));
    }

  };
}

let hasWarned = false;
export function checkoutPair(documentStore, idPair) {
  if (!hasWarned) {
    // eslint-disable-next-line no-console
    console.warn('[deprecation] The checkout() function has been deprecated in favor of checkoutPair()');
    hasWarned = true;
  }

  const {
    draft,
    published
  } = documentStore.checkoutPair(idPair);
  return {
    draft: wrap(draft),
    published: wrap(published)
  };
}