import { useMemoObservable } from 'react-rx';
import { map } from 'rxjs/operators';
import { useDocumentStore } from '../datastores';
const SYNCING = {
  isSyncing: true
};
const NOT_SYNCING = {
  isSyncing: false
};
export function useSyncState(publishedDocId, documentType) {
  const documentStore = useDocumentStore();
  return useMemoObservable(() => documentStore.pair.consistencyStatus(publishedDocId, documentType).pipe(map(isConsistent => isConsistent ? NOT_SYNCING : SYNCING)), [publishedDocId], NOT_SYNCING);
}