import { useMemoObservable } from 'react-rx';
import { useDocumentStore } from '../datastores';
const INITIAL = {
  validation: [],
  isValidating: false
};
export function useValidationStatus(publishedDocId, docTypeName) {
  const documentStore = useDocumentStore();
  return useMemoObservable(() => documentStore.pair.validation(publishedDocId, docTypeName), [publishedDocId, docTypeName], INITIAL);
}