import { isNonNullable } from '../../util/isNonNullable';

const isDocumentType = type => Boolean(type.type && type.type.name === 'document');

const isSanityType = type => type.name.startsWith('sanity.');

export const getSearchableTypes = schema => schema.getTypeNames().map(typeName => schema.get(typeName)).filter(isNonNullable).filter(schemaType => isDocumentType(schemaType)).filter(type => !isSanityType(type));