export function renderPath(path) {
  return path.map(segment => {
    if (segment.kind === 'type') {
      return "".concat(segment.name || '<unnamed>', "(").concat(segment.type, ")");
    }

    if (segment.kind === 'property') {
      return segment.name;
    }

    return null;
  }).filter(Boolean).join(' > ');
}